import React from 'react'

const CommentItem = (props) => {
  return (
    <div className='comment'>
        <div className='header'>
            <span className='icon'>
                {props.comment.name[0].toUpperCase()}
            </span>
            <h4>{props.comment.name.slice(0,6)}</h4>

        </div>

    </div>
  )
}

export default CommentItem
