import React, { useEffect, useState } from "react";
import "./App.css";
import PostList from "./components/PostList";
import PostForm from "./components/PostForm";
import PostFilter from "./components/PostFilter";
import MyModal from "./components/UI/modal/MyModal";
import RoundButton from "./components/UI/roundButton/RoundButton";
import MainButton from "./components/UI/button/MainButton";
import { usePosts } from "./hooks/usePosts";
import PostService from "./API/PostService";
import MyLoader from "./components/UI/loader/MyLoader";
import { useFetching } from "./hooks/useFetching";
import { getPageCount } from "./utils/pages";

const fontLink = document.createElement("link");
fontLink.href =
  "https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;500&display=swap";
fontLink.rel = "stylesheet";
document.head.appendChild(fontLink);

function App() {
  const [posts, setPosts] = useState([]);
  const [filter, setFilter] = useState({ sort: "", query: "" });
  const [modal, setModal] = useState(false);
  const [totalPages, setTotalPages] = useState(0);
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(1);
  const sortedAndSearchedPosts = usePosts(posts, filter.sort, filter.query);
  let pagesArray = []
  for (let i = 0; i < totalPages; i++) {
    pagesArray.push(i + 1);

  }

  const [fetchPosts, isPostsLoading, postError] = useFetching( async () => {
    const response = await PostService.getAll(limit, page);
    setPosts(response.data);
    const totalCount = response.headers['x-total-count']
    setTotalPages(getPageCount(totalCount, limit))
  })

  useEffect(()=> {
    fetchPosts()
  }, [])

  const createPost = (newPost) => {
    setPosts([...posts, newPost]);
    setModal(false);
  };

  //получаем пост из дочернего компонента
  const removePost = (post) => {
    setPosts(posts.filter((p) => p.id !== post.id));
  };

  return (
    <div className="App">
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost} title="Create post" />
      </MyModal>
      <PostFilter filter={filter} setFilter={setFilter} />

      {postError && 
        <h3 style={{ textAlign: "center", marginTop: '20px', color: 'darkred', }}>Error: {postError}</h3>
      }

      {isPostsLoading
        ? <div style={{display: 'flex', justifyContent: 'center'}}> <MyLoader/> </div>
        : <PostList remove={removePost} posts={sortedAndSearchedPosts} title="Posts" />
      }


      <MainButton
        style={{ position: "fixed", right: 30, bottom: 30, borderRadius: "50%", height: "4rem", width: "4rem", }}
        onClick={() => setModal(true)}
      >
        <span style={{ fontSize: "2.5rem", fontWeight: "300" }}>+</span>
      </MainButton>
    </div>
  );
}

export default App;
